import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/replitAuth.ts.content:
import * as client from "openid-client";
import { Strategy, type VerifyFunction } from "openid-client/passport";

import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import memoize from "memoizee";
import connectPg from "connect-pg-simple";
import { storage } from "./storage";

if (!process.env.REPLIT_DOMAINS) {
  throw new Error("Environment variable REPLIT_DOMAINS not provided");
}

const getOidcConfig = memoize(
  async () => {
    return await client.discovery(
      new URL(process.env.ISSUER_URL ?? "https://replit.com/oidc"),
      process.env.REPL_ID!
    );
  },
  { maxAge: 3600 * 1000 }
);

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: false,
    ttl: sessionTtl,
    tableName: "sessions",
  });
  return session({
    secret: process.env.SESSION_SECRET!,
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: true,
      maxAge: sessionTtl,
    },
  });
}

function updateUserSession(
  user: any,
  tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers
) {
  user.claims = tokens.claims();
  user.access_token = tokens.access_token;
  user.refresh_token = tokens.refresh_token;
  user.expires_at = user.claims?.exp;
}

async function upsertUser(
  claims: any,
) {
  await storage.upsertUser({
    id: claims["sub"],
    email: claims["email"],
    firstName: claims["first_name"],
    lastName: claims["last_name"],
    profileImageUrl: claims["profile_image_url"],
  });
}

export async function setupAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  const config = await getOidcConfig();

  const verify: VerifyFunction = async (
    tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
    verified: passport.AuthenticateCallback
  ) => {
    const user = {};
    updateUserSession(user, tokens);
    await upsertUser(tokens.claims());
    verified(null, user);
  };

  for (const domain of process.env
    .REPLIT_DOMAINS!.split(",")) {
    const strategy = new Strategy(
      {
        name: `replitauth:${domain}`,
        config,
        scope: "openid email profile offline_access",
        callbackURL: `https://${domain}/api/callback`,
      },
      verify,
    );
    passport.use(strategy);
  }

  passport.serializeUser((user: Express.User, cb) => cb(null, user));
  passport.deserializeUser((user: Express.User, cb) => cb(null, user));

  app.get("/api/login", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      prompt: "login consent",
      scope: ["openid", "email", "profile", "offline_access"],
    })(req, res, next);
  });

  app.get("/api/callback", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      successReturnToOrRedirect: "/",
      failureRedirect: "/api/login",
    })(req, res, next);
  });

  app.get("/api/logout", (req, res) => {
    req.logout(() => {
      res.redirect(
        client.buildEndSessionUrl(config, {
          client_id: process.env.REPL_ID!,
          post_logout_redirect_uri: `${req.protocol}://${req.hostname}`,
        }).href
      );
    });
  });
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  const user = req.user as any;

  if (!req.isAuthenticated() || !user.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now <= user.expires_at) {
    return next();
  }

  const refreshToken = user.refresh_token;
  if (!refreshToken) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }

  try {
    const config = await getOidcConfig();
    const tokenResponse = await client.refreshTokenGrant(config, refreshToken);
    updateUserSession(user, tokenResponse);
    return next();
  } catch (error) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }
};

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { setupAuth, isAuthenticated } from "./emailAuth";
import { z } from "zod";
import {
  insertClassTypeSchema,
  insertExerciseSchema,
  insertRoutineSchema,
  insertRoutineExerciseSchema,
  insertCalendarEventSchema,
  insertClientSchema,
  insertClientNoteSchema,
  insertAttendanceSchema,
  insertProgressMetricSchema,
  insertProgramSchema,
  insertProgramSessionSchema,
  insertProgramEnrollmentSchema,
  insertEventTargetSchema,
  insertReadinessCheckSchema,
  insertPerformanceRecordSchema
} from "@shared/schema";

export async function registerRoutes(app: Express): Promise<Server> {
  // Setup authentication
  await setupAuth(app);

  // Manual seed endpoint for existing users (force refresh)
  app.post('/api/seed-data', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.id;
      console.log(`Manual seed/refresh requested for user: ${userId}`);

      // Force seed and return current counts
      await storage.seedDefaultData(userId);

      // Also force exercise creation for existing class types that might not have exercises
      const classTypes = await storage.getClassTypes(userId);
      for (const classType of classTypes) {
        const existingExercises = await storage.getExercises({ classType: classType.id, userId });
        if (existingExercises.length === 0) {
          console.log(`Creating missing exercises for class type: ${classType.name}`);
          await storage.createDefaultExercisesForClass(classType, userId);
        }
      }

      // Get current data counts
      const finalExercises = await storage.getExercises(userId);

      res.json({ 
        message: "Data refresh completed", 
        classTypes: classTypes.length,
        exercises: finalExercises.length
      });
    } catch (error) {
      console.error("Error seeding data:", error);
      res.status(500).json({ message: "Failed to seed data" });
    }
  });

  // Create exercises for existing class types
  app.post('/api/create-exercises-for-existing-classes', async (req, res) => {
    try {
      console.log("Creating exercises for existing class types...");

      // Get all users
      const allUsers = await storage.getAllUsers();
      let totalExercisesCreated = 0;

      for (const user of allUsers) {
        const classTypes = await storage.getClassTypes(user.id);
        console.log(`Processing ${classTypes.length} class types for user: ${user.id}`);

        for (const classType of classTypes) {
          try {
            console.log(`Creating exercises for ${classType.name}...`);
            await storage.createDefaultExercisesForClass(classType, user.id);
            totalExercisesCreated += 10; // Each class type gets 10 exercises
          } catch (error) {
            console.error(`Failed to create exercises for ${classType.name}:`, error);
          }
        }
      }

      res.json({ message: `Created ${totalExercisesCreated} exercises successfully` });
    } catch (error) {
      console.error("Error creating exercises:", error);
      res.status(500).json({ message: "Failed to create exercises" });
    }
  });

  // Emergency seed endpoint for production (admin use)
  app.post('/api/force-seed-all', async (req, res) => {
    try {
      // This endpoint can be called without auth for production setup
      console.log("Force seeding all default data...");

      // Get all users and seed them
      const allUsers = await storage.getAllUsers();
      console.log(`Found ${allUsers.length} users to seed`);

      for (const user of allUsers) {
        try {
          console.log(`Force seeding for user: ${user.id}`);
          await storage.seedDefaultData(user.id);

          // Also ensure exercises exist for all class types
          const classTypes = await storage.getClassTypes(user.id);
          for (const classType of classTypes) {
            const existingExercises = await storage.getExercises({ classType: classType.id, userId: user.id });
            if (existingExercises.length === 0) {
              console.log(`Creating missing exercises for class type: ${classType.name} (user: ${user.id})`);
              await storage.createDefaultExercisesForClass(classType, user.id);
            }
          }
        } catch (error) {
          console.error(`Failed to seed user ${user.id}:`, error);
        }
      }

      res.json({ message: `Seeded ${allUsers.length} users successfully` });
    } catch (error) {
      console.error("Error in force seed:", error);
      res.status(500).json({ message: "Failed to force seed" });
    }
  });

  // Cleanup duplicate class types endpoint
  app.post('/api/cleanup-duplicate-class-types', async (req, res) => {
    try {
      console.log("Starting duplicate class types cleanup...");

      const result = await storage.cleanupDuplicateClassTypes();

      console.log("Cleanup completed:", result);

      res.json({
        message: "Duplicate class types cleanup completed",
        duplicatesFound: result.duplicatesFound,
        duplicatesRemoved: result.duplicatesRemoved,
        report: result.report
      });
    } catch (error) {
      console.error("Error in cleanup:", error);
      res.status(500).json({ message: "Failed to cleanup duplicates" });
    }
  });

  // Auth routes
  app.get('/api/auth/user', isAuthenticated, async (req: any, res) => {
    try {
      // req.user is already the full user object from our auth middleware
      const user = req.user;

      // Seed default data for new users (non-blocking)
      console.log(`Checking/seeding default data for user: ${user.id}`);
      storage.seedDefaultData(user.id).then(() => {
        console.log(`Default data seeding completed for user: ${user.id}`);
      }).catch(error => {
        console.error("Error seeding default data:", error);
      });

      res.json({
        id: user.id,
        email: user.email,
        firstName: user.firstName,
        lastName: user.lastName,
        profileImageUrl: user.profileImageUrl,
      });
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // Dashboard stats
  app.get('/api/dashboard/stats', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.id;
      const stats = await storage.getUserStats(userId);
      res.json(stats);
    } catch (error) {
      console.error("Error fetching stats:", error);
      res.status(500).json({ message: "Failed to fetch stats" });
    }
  });

  // Analytics data
  app.get('/api/dashboard/analytics', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.id;
      const analytics = await storage.getAnalyticsData(userId);
      res.json(analytics);
    } catch (error) {
      console.error("Error fetching analytics:", error);
      res.status(500).json({ message: "Failed to fetch analytics" });
    }
  });

  // Class Types routes
  app.get('/api/class-types', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.id;
      console.log(`Fetching class types for user: ${userId}`);

      // Add cache-busting headers for production
      res.set({
        'Cache-Control': 'no-cache, no-store, must-revalidate',
        'Pragma': 'no-cache',
        'Expires': '0'
      });

      const classTypes = await storage.getClassTypes(userId);
      console.log(`Found ${classTypes.length} class types for user: ${userId}`);

      res.json(classTypes);
    } catch (error) {
      console.error("Error fetching class types:", error);
      res.status(500).json({ message: "Failed to fetch class types" });
    }
  });

  app.post('/api/class-types', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.id;
      const data = insertClassTypeSchema.parse({
        ...req.body,
        createdByUserId: userId,
      });
      const classType = await storage.createClassType(data);

      // Create default exercises for this class type
      await storage.createDefaultExercisesForClass(classType, userId);

      res.status(201).json(classType);
    } catch (error) {
      console.error("Error creating class type:", error);
      res.status(400).json({ message: "Failed to create class type" });
    }
  });

  app.patch('/api/class-types/:id', isAuthenticated, async (req, res) => {
    try {
      const { id } = req.params;
      const data = insertClassTypeSchema.partial().parse(req.body);
      const classType = await storage.updateClassType(id, data);
      res.json(classType);
    } catch (error) {
      console.error("Error updating class type:", error);
      res.status(400).json({ message: "Failed to update class type" });
    }
  });

  app.delete('/api/class-types/:id', isAuthenticated, async (req, res) => {
    try {
      const { id } = req.params;
      await storage.deleteClassType(id);
      res.status(204).send();
    } catch (error) {
      console.error("Error deleting class type:", error);
      res.status(400).json({ message: "Failed to delete class type" });
    }
  });

  // Exercises routes with detailed debugging
  app.get('/api/exercises', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.id;
      const { search, category, difficulty, equipment, classType } = req.query;

      console.log(`[DEBUG] Fetching exercises for user: ${userId}, filters:`, { search, category, difficulty, equipment, classType });
      console.log(`[DEBUG] Database URL exists: ${!!process.env.DATABASE_URL}`);
      console.log(`[DEBUG] Environment: ${process.env.NODE_ENV}`);

      // Add cache-busting headers for production
      res.set({
        'Cache-Control': 'no-cache, no-store, must-revalidate',
        'Pragma': 'no-cache',
        'Expires': '0'
      });

      const exercises = await storage.getExercises({
        search: search as string,
        category: category as string,
        difficulty: difficulty as string,
        equipment: equipment as string,
        classType: classType as string,
        userId,
      });

      // Debug first few exercises
      console.log(`[DEBUG] Found ${exercises.length} exercises for user: ${userId}`);
      console.log(`[DEBUG] First 3 exercises:`, exercises.slice(0, 3).map(ex => ({ id: ex.id.slice(0, 8), name: ex.name, classTypeId: ex.classTypeId?.slice(0, 8) })));
      console.log(`[DEBUG] Any 'Reach' exercises:`, exercises.filter(ex => ex.name.toLowerCase().includes('reach')).length);

      res.json(exercises);
    } catch (error) {
      console.error("Error fetching exercises:", error);
      res.status(500).json({ message: "Failed to fetch exercises" });
    }
  });

  app.get('/api/exercises/:id', isAuthenticated, async (req, res) => {
    try {
      const { id } = req.params;
      const exercise = await storage.getExercise(id);
      if (!exercise) {
        return res.status(404).json({ message: "Exercise not found" });
      }
      res.json(exercise);
    } catch (error) {
      console.error("Error fetching exercise:", error);
      res.status(500).json({ message: "Failed to fetch exercise" });
    }
  });

  app.post('/api/exercises', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.id;
      const data = insertExerciseSchema.parse({
        ...req.body,
        createdByUserId: userId,
      });
      const exercise = await storage.createExercise(data);
      res.status(201).json(exercise);
    } catch (error) {
      console.error("Error creating exercise:", error);
      res.status(400).json({ message: "Failed to create exercise" });
    }
  });

  app.put('/api/exercises/:id', isAuthenticated, async (req, res) => {
    try {
      const { id } = req.params;
      const data = insertExerciseSchema.partial().parse(req.body);
      const exercise = await storage.updateExercise(id, data);
      res.json(exercise);
    } catch (error) {
      console.error("Error updating exercise:", error);
      res.status(400).json({ message: "Failed to update exercise" });
    }
  });

  app.delete('/api/exercises/:id', isAuthenticated, async (req, res) => {
    try {
      const { id } = req.params;
      await storage.deleteExercise(id);
      res.status(204).send();
    } catch (error) {
      console.error("Error deleting exercise:", error);
      res.status(400).json({ message: "Failed to delete exercise" });
    }
  });

  // Routines routes
  app.get('/api/routines', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.id;
      const routines = await storage.getRoutines(userId);
      res.json(routines);
    } catch (error) {
      console.error("Error fetching routines:", error);
      res.status(500).json({ message: "Failed to fetch routines" });
    }
  });

  app.get('/api/routines/:id', isAuthenticated, async (req, res) => {
    try {
      const { id } = req.params;
      const routine = await storage.getRoutineWithExercises(id);
      if (!routine) {
        return res.status(404).json({ message: "Routine not found" });
      }
      res.json(routine);
    } catch (error) {
      console.error("Error fetching routine:", error);
      res.status(500).json({ message: "Failed to fetch routine" });
