      const targets = await storage.getEventTargets(req.params.eventId);
      res.json(targets);
    } catch (error) {
      console.error("Error fetching event targets:", error);
      res.status(500).json({ message: "Failed to fetch event targets" });
    }
  });

  app.post('/api/events/:eventId/targets', isAuthenticated, async (req: any, res) => {
    try {
      const targetsData = req.body.map((target: any) => ({
        ...insertEventTargetSchema.parse(target),
        eventId: req.params.eventId,
      }));
      const targets = await storage.createEventTargets(targetsData);
      res.status(201).json(targets);
    } catch (error) {
      console.error("Error creating event targets:", error);
      res.status(400).json({ message: "Failed to create event targets", error: error instanceof Error ? error.message : "Unknown error" });
    }
  });

  app.patch('/api/event-targets/:id', isAuthenticated, async (req: any, res) => {
    try {
      const updateData = insertEventTargetSchema.partial().parse(req.body);
      const target = await storage.updateEventTarget(req.params.id, updateData);
      res.json(target);
    } catch (error) {
      console.error("Error updating event target:", error);
      res.status(400).json({ message: "Failed to update event target", error: error instanceof Error ? error.message : "Unknown error" });
    }
  });

  // Readiness Check Routes
  app.get('/api/clients/:clientId/readiness', isAuthenticated, async (req: any, res) => {
    try {
      const date = req.query.date ? new Date(req.query.date as string) : undefined;
      const readiness = await storage.getClientReadiness(req.params.clientId, date);
      res.json(readiness);
    } catch (error) {
      console.error("Error fetching client readiness:", error);
      res.status(500).json({ message: "Failed to fetch client readiness" });
    }
  });

  app.post('/api/clients/:clientId/readiness', isAuthenticated, async (req: any, res) => {
    try {
      const readinessData = insertReadinessCheckSchema.parse({
        ...req.body,
        clientId: req.params.clientId,
        date: new Date(req.body.date),
      });
      const readiness = await storage.createReadinessCheck(readinessData);
      res.status(201).json(readiness);
    } catch (error) {
      console.error("Error creating readiness check:", error);
      res.status(400).json({ message: "Failed to create readiness check", error: error instanceof Error ? error.message : "Unknown error" });
    }
  });

  app.get('/api/clients/:clientId/readiness/latest', isAuthenticated, async (req: any, res) => {
    try {
      const readiness = await storage.getLatestReadiness(req.params.clientId);
      res.json(readiness);
    } catch (error) {
      console.error("Error fetching latest readiness:", error);
      res.status(500).json({ message: "Failed to fetch latest readiness" });
    }
  });

  // Performance Record Routes
  app.get('/api/events/:eventId/performance', isAuthenticated, async (req: any, res) => {
    try {
      const clientId = req.query.clientId as string;
      const performance = await storage.getPerformanceRecords(req.params.eventId, clientId);
      res.json(performance);
    } catch (error) {
      console.error("Error fetching performance records:", error);
      res.status(500).json({ message: "Failed to fetch performance records" });
    }
  });

  app.post('/api/events/:eventId/performance', isAuthenticated, async (req: any, res) => {
    try {
      const performanceData = insertPerformanceRecordSchema.parse({
        ...req.body,
        eventId: req.params.eventId,
      });
      const performance = await storage.createPerformanceRecord(performanceData);
      res.status(201).json(performance);
    } catch (error) {
      console.error("Error creating performance record:", error);
      res.status(400).json({ message: "Failed to create performance record", error: error instanceof Error ? error.message : "Unknown error" });
    }
  });

  // Apply Progression to Event
  app.post('/api/events/:eventId/apply-progression', isAuthenticated, async (req: any, res) => {
    try {
      const result = await storage.applyProgression(req.params.eventId);
      res.json(result);
    } catch (error) {
      console.error("Error applying progression:", error);
      res.status(500).json({ message: "Failed to apply progression", error: error instanceof Error ? error.message : "Unknown error" });
    }
  });

  // Simple fix: Use reseed endpoint to populate production with correct exercises
  app.post('/api/fix-exercises', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.id;
      console.log('[FIX] Fixing exercises for user:', userId);

      // Import and run the seeder
      const { seedExercises } = await import('./seed-exercises');
      const totalCreated = await seedExercises(userId);

      // Get current counts after seeding
      const allExercises = await storage.getExercises(userId);
      const allClassTypes = await storage.getClassTypes(userId);

      console.log('[FIX] Fix completed. Total exercises:', allExercises.length);

      res.json({
        success: true,
        message: `Exercise fix completed! Created ${totalCreated} exercises.`,
        totalExercises: allExercises.length,
        totalClassTypes: allClassTypes.length,
        timestamp: new Date().toISOString(),
        environment: process.env.NODE_ENV || 'production'
      });
    } catch (error: any) {
      console.error('[FIX] Error:', error);
      res.status(500).json({ 
        success: false,
        error: error.message, 
        timestamp: new Date().toISOString()
      });
    }
  });

  // Clean up duplicate exercises endpoint
  app.post('/api/cleanup-exercises', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.id;
      console.log('[CLEANUP] Starting exercise cleanup for user:', userId);

      // Get all exercises for the user
      const allExercises = await storage.getExercises({ userId });

      // Group by name (case-insensitive)
      const exercisesByName = new Map<string, any[]>();
      for (const exercise of allExercises) {
        const key = exercise.name.toLowerCase().trim();
        if (!exercisesByName.has(key)) {
          exercisesByName.set(key, []);
        }
        exercisesByName.get(key)!.push(exercise);
      }

      let duplicatesRemoved = 0;

      // For each group, keep the first one and delete the rest
      for (const [name, exercises] of exercisesByName.entries()) {
        if (exercises.length > 1) {
          console.log(`[CLEANUP] Found ${exercises.length} duplicates of "${exercises[0].name}"`);

          // Keep the first exercise, delete the rest
          for (let i = 1; i < exercises.length; i++) {
            await storage.deleteExercise(exercises[i].id);
            duplicatesRemoved++;
          }
        }
      }

      const remainingExercises = await storage.getExercises({ userId });

      console.log('[CLEANUP] Cleanup completed. Removed:', duplicatesRemoved, 'Total remaining:', remainingExercises.length);

      res.json({
        success: true,
        message: `Cleanup completed! Removed ${duplicatesRemoved} duplicate exercises.`,
        duplicatesRemoved,
        totalExercises: remainingExercises.length,
        timestamp: new Date().toISOString()
      });
    } catch (error: any) {
      console.error('[CLEANUP] Error:', error);
      res.status(500).json({ 
        success: false,
        error: error.message, 
        timestamp: new Date().toISOString()
      });
    }
  });

  // Clear all exercises endpoint
  app.post('/api/clear-exercises', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.id;
      console.log('[CLEAR] Clearing all exercises for user:', userId);

      // Get all exercises for the user and delete them
      const allExercises = await storage.getExercises({ userId });

      for (const exercise of allExercises) {
        await storage.deleteExercise(exercise.id);
      }

      console.log(`[CLEAR] Cleared ${allExercises.length} exercises for user: ${userId}`);

      res.json({
        success: true,
        message: `Cleared ${allExercises.length} exercises from database.`,
        exercisesRemoved: allExercises.length,
        timestamp: new Date().toISOString()
      });
    } catch (error: any) {
      console.error('[CLEAR] Error:', error);
      res.status(500).json({ 
        success: false,
        error: error.message, 
        timestamp: new Date().toISOString()
      });
    }
  });

  // Reseed exercises endpoint
  app.post('/api/reseed-exercises', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.id;
      console.log('[RESEED] Starting exercise reseed for user:', userId);

      const { seedExercises } = await import('./seed-exercises');
      const totalCreated = await seedExercises(userId);

      console.log('[RESEED] Successfully created exercises:', totalCreated);
      res.json({
        success: true,
        message: `Successfully created ${totalCreated} exercises`,
        totalCreated,
        timestamp: new Date().toISOString()
      });
    } catch (error) {
      console.error('[RESEED] Error:', error);
      res.status(500).json({ 
        success: false, 
        error: error.message, 
        timestamp: new Date().toISOString() 
      });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

