import React from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { type ClassType, type Routine, type CalendarEvent } from "@shared/schema";

const eventFormSchema = z.object({
  classTypeId: z.string().min(1, "Class type is required"),
  routineId: z.string().optional(),
  eventDate: z.string().min(1, "Event date is required"),
  startHour: z.string().min(1, "Start hour is required"),
  startMinute: z.string().min(1, "Start minute is required"),
  duration: z.string().min(1, "Duration is required"),
  location: z.string().optional(),
  notes: z.string().optional(),
});

type EventFormData = z.infer<typeof eventFormSchema>;

interface CalendarEventFormProps {
  classTypes: ClassType[] | undefined;
  routines: Routine[] | undefined;
  selectedDate: Date;
  onSubmit: (data: EventFormData) => void;
  onCancel: () => void;
  isLoading?: boolean;
  editingEvent?: CalendarEvent | null;
}

export function CalendarEventForm({ 
  classTypes, 
  routines, 
  selectedDate, 
  onSubmit, 
  onCancel, 
  isLoading = false,
  editingEvent = null
}: CalendarEventFormProps) {
  const form = useForm<EventFormData>({
    resolver: zodResolver(eventFormSchema),
    defaultValues: {
      classTypeId: "",
      routineId: "none",
      eventDate: `${selectedDate.getFullYear()}-${String(selectedDate.getMonth() + 1).padStart(2, '0')}-${String(selectedDate.getDate()).padStart(2, '0')}`,
      startHour: "09",
      startMinute: "00",
      duration: "60",
      location: "",
      notes: "",
    },
  });

  // Update form when editing event or date changes
  React.useEffect(() => {
    if (editingEvent) {
      const startDate = new Date(editingEvent.startDatetime);
      const endDate = new Date(editingEvent.endDatetime);
      const duration = Math.round((endDate.getTime() - startDate.getTime()) / (60 * 1000));

      form.reset({
        classTypeId: editingEvent.classTypeId || "",
        routineId: editingEvent.routineId || "none",
        eventDate: `${startDate.getFullYear()}-${String(startDate.getMonth() + 1).padStart(2, '0')}-${String(startDate.getDate()).padStart(2, '0')}`,
        startHour: startDate.getHours().toString().padStart(2, '0'),
        startMinute: startDate.getMinutes().toString().padStart(2, '0'),
        duration: duration.toString(),
        location: editingEvent.location || "",
        notes: editingEvent.notes || "",
      });
    } else {
      form.setValue('eventDate', `${selectedDate.getFullYear()}-${String(selectedDate.getMonth() + 1).padStart(2, '0')}-${String(selectedDate.getDate()).padStart(2, '0')}`);
    }
  }, [selectedDate, editingEvent, form]);

  return (
    <Form {...form}>
      <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
        <FormField
          control={form.control}
          name="classTypeId"
          render={({ field }) => (
            <FormItem>
              <FormLabel>Class Type *</FormLabel>
              <Select onValueChange={field.onChange} value={field.value}>
                <FormControl>
                  <SelectTrigger data-testid="select-class-type">
                    <SelectValue placeholder="Select class type" />
                  </SelectTrigger>
                </FormControl>
                <SelectContent>
                  {classTypes?.map((classType) => (
                    <SelectItem key={classType.id} value={classType.id}>
                      {classType.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
              <FormMessage />
            </FormItem>
          )}
        />

        <FormField
          control={form.control}
          name="routineId"
          render={({ field }) => (
            <FormItem>
              <FormLabel>Routine (Optional)</FormLabel>
              <Select 
                onValueChange={(value) => field.onChange(value === "none" ? "" : value)} 
                value={field.value || "none"}
              >
                <FormControl>
                  <SelectTrigger data-testid="select-routine">
                    <SelectValue placeholder="Select a routine" />
                  </SelectTrigger>
                </FormControl>
                <SelectContent>
                  <SelectItem value="none">No routine</SelectItem>
                  {routines?.map((routine) => (
                    <SelectItem key={routine.id} value={routine.id}>
                      {routine.name} {routine.totalDuration ? `(${Math.round(routine.totalDuration / 60)} min)` : ''}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
              <FormMessage />
            </FormItem>
          )}
        />

        <FormField
          control={form.control}
          name="eventDate"
          render={({ field }) => (
            <FormItem>
              <FormLabel>Event Date</FormLabel>
              <FormControl>
                <Input 
                  type="date" 
                  {...field} 
                  data-testid="input-event-date"
                />
              </FormControl>
              <FormMessage />
            </FormItem>
          )}
        />

        <div className="grid grid-cols-3 gap-4">
          <FormField
            control={form.control}
            name="startHour"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Hour</FormLabel>
                <Select onValueChange={field.onChange} value={field.value}>
                  <FormControl>
                    <SelectTrigger data-testid="select-start-hour">
                      <SelectValue placeholder="Hour" />
                    </SelectTrigger>
                  </FormControl>
                  <SelectContent>
                    {Array.from({ length: 24 }, (_, i) => (
                      <SelectItem key={i} value={i.toString().padStart(2, '0')}>
                        {i === 0 ? '12 AM' : i < 12 ? `${i} AM` : i === 12 ? '12 PM' : `${i - 12} PM`}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
                <FormMessage />
              </FormItem>
            )}
          />

          <FormField
            control={form.control}
            name="startMinute"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Minute</FormLabel>
                <Select onValueChange={field.onChange} value={field.value}>
                  <FormControl>
                    <SelectTrigger data-testid="select-start-minute">
                      <SelectValue placeholder="Min" />
                    </SelectTrigger>
                  </FormControl>
                  <SelectContent>
                    {['00', '15', '30', '45'].map((minute) => (
                      <SelectItem key={minute} value={minute}>
                        {minute}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
                <FormMessage />
              </FormItem>
            )}
          />

          <FormField
            control={form.control}
            name="duration"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Duration</FormLabel>
                <Select onValueChange={field.onChange} value={field.value}>
                  <FormControl>
                    <SelectTrigger data-testid="select-duration">
                      <SelectValue placeholder="Duration" />
                    </SelectTrigger>
                  </FormControl>
                  <SelectContent>
                    <SelectItem value="15">15 min</SelectItem>
                    <SelectItem value="30">30 min</SelectItem>
                    <SelectItem value="45">45 min</SelectItem>
                    <SelectItem value="60">1 hour</SelectItem>
                    <SelectItem value="75">1h 15m</SelectItem>
                    <SelectItem value="90">1h 30m</SelectItem>
                    <SelectItem value="105">1h 45m</SelectItem>
                    <SelectItem value="120">2 hours</SelectItem>
                    <SelectItem value="150">2h 30m</SelectItem>
                    <SelectItem value="180">3 hours</SelectItem>
                  </SelectContent>
                </Select>
                <FormMessage />
              </FormItem>
            )}
          />
        </div>

        <FormField
          control={form.control}
          name="location"
          render={({ field }) => (
            <FormItem>
              <FormLabel>Location (Optional)</FormLabel>
              <FormControl>
                <Input 
                  placeholder="Studio A, Gym, Online, etc." 
                  {...field} 
                  data-testid="input-event-location" 
                />
              </FormControl>
              <FormMessage />
            </FormItem>
          )}
        />

        <FormField
          control={form.control}
          name="notes"
          render={({ field }) => (
            <FormItem>
              <FormLabel>Notes (Optional)</FormLabel>
              <FormControl>
                <Input 
                  placeholder="Additional notes..." 
                  {...field} 
                  data-testid="input-event-notes" 
                />
              </FormControl>
              <FormMessage />
            </FormItem>
          )}
        />

        <div className="flex justify-end space-x-2">
          <Button 
            type="button" 
            variant="outline" 
            onClick={onCancel}
            data-testid="button-cancel-event"
          >
            Cancel
          </Button>
          <Button 
            type="submit" 
            disabled={isLoading}
            data-testid="button-create-event"
          >
            {isLoading ? (editingEvent ? "Updating..." : "Creating...") : (editingEvent ? "Update Event" : "Create Event")}
          </Button>
        </div>
      </form>
    </Form>
  );
}

export default CalendarEventForm;

client/src/components/class-enrollment.t.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Checkbox } from "@/components/ui/checkbox";
import { Badge } from "@/components/ui/badge";
import { Users, UserPlus, UserCheck, UserX } from "lucide-react";

interface ClassEnrollmentProps {
  eventId: string;
  eventTitle: string;
}

export function ClassEnrollment({ eventId, eventTitle }: ClassEnrollmentProps) {
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch all clients
  const { data: allClients = [] } = useQuery({
    queryKey: ["/api/clients"],
  });

  // Fetch enrolled clients for this event
  const { data: enrolledClients = [] } = useQuery({
    queryKey: ["/api/calendar/events", eventId, "clients"],
  });

  // Enroll/unenroll mutation
  const enrollmentMutation = useMutation({
    mutationFn: async ({ clientId, action }: { clientId: string; action: "enroll" | "unenroll" }) => {
      if (action === "enroll") {
        const response = await apiRequest("POST", `/api/calendar/events/${eventId}/clients`, {
          clientId,
        });
        return await response.json();
      } else {
        const response = await apiRequest("DELETE", `/api/calendar/events/${eventId}/clients/${clientId}`, {});
        return await response.json();
      }
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/calendar/events", eventId, "clients"] });
      toast({
        title: "Success",
        description: "Client enrollment updated successfully.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update client enrollment.",
        variant: "destructive",
      });
    },
  });

  const handleEnrollmentToggle = (clientId: string, isEnrolled: boolean) => {
    enrollmentMutation.mutate({
      clientId,
      action: isEnrolled ? "unenroll" : "enroll",
    });
  };

  const enrolledClientIds = new Set(enrolledClients.map((client: any) => client.id));

  return (
    <div>
      <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
        <DialogTrigger asChild>
          <Button variant="outline" size="sm" data-testid="button-manage-enrollment">
            <Users className="w-4 h-4 mr-2" />
            Manage Clients ({enrolledClients.length})
          </Button>
        </DialogTrigger>
        <DialogContent className="max-w-2xl">
          <DialogHeader>
            <DialogTitle>
              Manage Client Enrollment - {eventTitle}
            </DialogTitle>
          </DialogHeader>

          <div className="space-y-4">
            {/* Enrolled clients summary */}
            <Card>
              <CardHeader>
                <CardTitle className="text-sm flex items-center gap-2">
                  <UserCheck className="w-4 h-4" />
                  Enrolled Clients ({enrolledClients.length})
                </CardTitle>
              </CardHeader>
              <CardContent>
                {enrolledClients.length === 0 ? (
                  <p className="text-sm text-gray-500">No clients enrolled yet</p>
                ) : (
                  <div className="flex flex-wrap gap-2">
                    {enrolledClients.map((client: any) => (
                      <Badge key={client.id} variant="default">
                        {client.firstName} {client.lastName}
                      </Badge>
                    ))}
                  </div>
                )}
              </CardContent>
            </Card>

            {/* Client enrollment list */}
            <div className="max-h-96 overflow-y-auto">
              <h4 className="font-medium mb-3">Select Clients for This Class</h4>
              <div className="space-y-2">
                {allClients.map((client: any) => {
                  const isEnrolled = enrolledClientIds.has(client.id);
                  return (
                    <div
                      key={client.id}
                      className="flex items-center justify-between p-3 border rounded-lg"
                    >
                      <div className="flex items-center space-x-3">
                        <Checkbox
                          checked={isEnrolled}
                          onCheckedChange={() => handleEnrollmentToggle(client.id, isEnrolled)}
                          disabled={enrollmentMutation.isPending}
                          data-testid={`checkbox-client-${client.id}`}
                        />
                        <div>
                          <p className="font-medium">
                            {client.firstName} {client.lastName}
                          </p>
                          <p className="text-sm text-gray-500">
                            {client.email} â€¢ {client.fitnessLevel}
                          </p>
                        </div>
                      </div>
                      <div className="flex items-center gap-2">
                        {isEnrolled ? (
                          <Badge variant="default" className="bg-green-100 text-green-800">
                            <UserCheck className="w-3 h-3 mr-1" />
                            Enrolled
                          </Badge>
                        ) : (
                          <Badge variant="outline">
                            <UserPlus className="w-3 h-3 mr-1" />
                            Available
                          </Badge>
                        )}
                      </div>
                    </div>
                  );
                })}
              </div>
            </div>

            <div className="flex justify-end">
              <Button onClick={() => setIsDialogOpen(false)}>
                Done
              </Button>
            </div>
          </div>
        </DialogContent>
      </Dialog>
    </div>
  );
}

export default ClassEnrollment;

