import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Download, Share2, FileText, Link2, Mail, Copy, Check } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';

interface ExportRoutineProps {
  routineId: string;
  routineName: string;
  className?: string;
  openOnMount?: boolean;
}

interface Exercise {
  id: string;
  name: string;
  description?: string;
  duration: number;
  sets?: number;
  reps?: number;
  category: string;
  difficulty: string;
  equipment?: string;
}

interface Routine {
  id: string;
  name: string;
  description?: string;
  totalDuration: number;
  exerciseCount: number;
  classType?: {
    name: string;
  };
  exercises: Exercise[];
}

export default function ExportRoutine({ routineId, routineName, className, openOnMount = false }: ExportRoutineProps) {
  const [isOpen, setIsOpen] = useState(openOnMount);
  const [shareUrl, setShareUrl] = useState("");
  const [copied, setCopied] = useState(false);
  const [isExporting, setIsExporting] = useState(false);
  const { toast } = useToast();

  const { data: routine, isLoading } = useQuery<Routine>({
    queryKey: ["/api/routines", routineId],
    enabled: isOpen && !!routineId,
  });

  const formatDuration = (minutes: number) => {
    if (minutes < 60) return `${minutes}min`;
    const hours = Math.floor(minutes / 60);
    const mins = minutes % 60;
    return mins > 0 ? `${hours}h ${mins}min` : `${hours}h`;
  };

  const generateShareUrl = () => {
    const baseUrl = window.location.origin;
    const url = `${baseUrl}/shared/routine/${routineId}`;
    setShareUrl(url);
    return url;
  };

  const copyToClipboard = async (text: string) => {
    try {
      await navigator.clipboard.writeText(text);
      setCopied(true);
      toast({
        title: "Copied!",
        description: "Link copied to clipboard",
      });
      setTimeout(() => setCopied(false), 2000);
    } catch (err) {
      toast({
        title: "Error",
        description: "Failed to copy to clipboard",
        variant: "destructive",
      });
    }
  };

  const exportToPDF = async () => {
    if (!routine) {
      toast({
        title: "Export Failed",
        description: "No routine data available for export",
        variant: "destructive",
      });
      return;
    }

    console.log("Starting PDF export for routine:", routine);
    setIsExporting(true);
    try {
      const pdf = new jsPDF();
      const pageWidth = pdf.internal.pageSize.getWidth();
      const pageHeight = pdf.internal.pageSize.getHeight();
      const margin = 20;
      let yPosition = margin;

      // Header - Routine Name
      pdf.setFontSize(20);
      pdf.setFont("helvetica", "bold");
      pdf.text(routine.name || "Untitled Routine", margin, yPosition);
      yPosition += 12;

      // Class Type
      if (routine.classType?.name) {
        pdf.setFontSize(14);
        pdf.setFont("helvetica", "normal");
        pdf.text(`Class Type: ${routine.classType.name}`, margin, yPosition);
        yPosition += 10;
      }

      // Routine Summary
      pdf.setFontSize(12);
      pdf.setFont("helvetica", "normal");
      const duration = routine.totalDuration ? formatDuration(routine.totalDuration) : "0min";
      const exerciseCount = routine.exercises ? routine.exercises.length : 0;
      pdf.text(`Duration: ${duration} | Exercises: ${exerciseCount}`, margin, yPosition);
      yPosition += 8;

      // Description
      if (routine.description) {
        const splitDescription = pdf.splitTextToSize(routine.description, pageWidth - 2 * margin);
        pdf.text(splitDescription, margin, yPosition);
        yPosition += splitDescription.length * 5 + 8;
      }

      yPosition += 5;

      // Only create table if there are exercises
      if (routine.exercises && routine.exercises.length > 0) {
        // Table Header with more compact columns
        const colWidths = [8, 35, 15, 12, 12, 18, 20, 25, 15]; // Condensed column widths
        const rowHeight = 7; // Smaller row height

        // Draw header background
        pdf.setFillColor(240, 240, 240);
        pdf.rect(margin, yPosition - 2, pageWidth - 2 * margin, rowHeight + 4, 'F');

        // Header text
        pdf.setFontSize(8); // Smaller font for headers
        pdf.setFont("helvetica", "bold");
        let xPosition = margin + 1;

        const headers = ['#', 'Exercise Name', 'Time', 'Sets', 'Reps', 'Difficulty', 'Equipment', 'Music/Song', 'BPM'];
        headers.forEach((header, index) => {
          pdf.text(header, xPosition, yPosition + 4);
          xPosition += colWidths[index];
        });

        yPosition += rowHeight + 4;

        // Table rows
        pdf.setFont("helvetica", "normal");
        pdf.setFontSize(7); // Smaller font for data

        routine.exercises.forEach((routineExercise, index) => {
          // Check if we need a new page
          if (yPosition + rowHeight > pageHeight - 30) {
            pdf.addPage();
            yPosition = margin;

            // Redraw header on new page
            pdf.setFillColor(240, 240, 240);
            pdf.rect(margin, yPosition - 2, pageWidth - 2 * margin, rowHeight + 4, 'F');

            pdf.setFont("helvetica", "bold");
            pdf.setFontSize(8);
            xPosition = margin + 1;
            headers.forEach((header, idx) => {
              pdf.text(header, xPosition, yPosition + 4);
              xPosition += colWidths[idx];
            });

            yPosition += rowHeight + 4;
            pdf.setFont("helvetica", "normal");
            pdf.setFontSize(7);
          }

          // Draw row border
          pdf.setDrawColor(200, 200, 200);
          pdf.rect(margin, yPosition - 2, pageWidth - 2 * margin, rowHeight + 4);

          // Extract exercise data from nested structure
          const exercise = routineExercise.exercise || routineExercise;
          const exerciseName = exercise.name || 'Unnamed Exercise';

          // Format duration properly - show seconds if less than 60, otherwise minutes
          let durationDisplay = '-';
          if (routineExercise.durationSeconds) {
            const seconds = routineExercise.durationSeconds;
            if (seconds < 60) {
              durationDisplay = `${seconds}s`;
            } else {
              const minutes = Math.round(seconds / 60);
              durationDisplay = `${minutes}min`;
            }
          } else if (exercise.duration) {
            durationDisplay = typeof exercise.duration === 'number' ? `${exercise.duration}min` : exercise.duration.toString();
          }

          const sets = routineExercise.sets || exercise.sets || '-';
          const reps = routineExercise.repetitions || exercise.reps || '-';
          const difficulty = exercise.difficultyLevel || exercise.difficulty || '-';
          const equipment = exercise.equipmentNeeded || exercise.equipment || '-';
          const music = routineExercise.musicTitle || '-';
          const bpm = routineExercise.musicNotes || '-';

          // Row data with truncation for long text
          xPosition = margin + 1;
          const rowData = [
            (index + 1).toString(),
            exerciseName.length > 20 ? exerciseName.substring(0, 17) + '...' : exerciseName,
            durationDisplay,
            sets.toString(),
            reps.toString(),
            difficulty.length > 10 ? difficulty.substring(0, 8) + '..' : difficulty,
            equipment.length > 12 ? equipment.substring(0, 10) + '..' : equipment,
            music.length > 15 ? music.substring(0, 13) + '..' : music,
            bpm.toString()
          ];

          rowData.forEach((data, idx) => {
            pdf.text(data || '', xPosition, yPosition + 4);
            xPosition += colWidths[idx];
          });

          yPosition += rowHeight + 4;
        });

        // Add exercise descriptions section if any exist
        const exercisesWithDescriptions = routine.exercises.filter(routineEx => {
          const exercise = routineEx.exercise || routineEx;
          return exercise.description;
        });

        if (exercisesWithDescriptions.length > 0) {
          yPosition += 10;

          // Check if we need a new page for descriptions
          if (yPosition > pageHeight - 60) {
            pdf.addPage();
            yPosition = margin;
          }

          pdf.setFontSize(10);
          pdf.setFont("helvetica", "bold");
          pdf.text("Exercise Descriptions:", margin, yPosition);
          yPosition += 8;

          pdf.setFontSize(8);
          pdf.setFont("helvetica", "normal");

          exercisesWithDescriptions.forEach((routineExercise, descIndex) => {
            const exercise = routineExercise.exercise || routineExercise;
            const exerciseIndex = routine.exercises.findIndex(ex => 
              (ex.exercise?.id || ex.id) === (exercise.id || routineExercise.exerciseId)
            ) + 1;

            if (yPosition > pageHeight - 25) {
              pdf.addPage();
              yPosition = margin;
            }

            pdf.setFont("helvetica", "bold");
            pdf.text(`${exerciseIndex}. ${exercise.name || 'Unnamed Exercise'}:`, margin, yPosition);
            yPosition += 5;

            pdf.setFont("helvetica", "normal");
            if (exercise.description) {
              const splitDesc = pdf.splitTextToSize(exercise.description, pageWidth - 2 * margin - 5);
              pdf.text(splitDesc, margin + 3, yPosition);
              yPosition += splitDesc.length * 3.5 + 6;
            }
          });
        }
      } else {
        // No exercises message
        pdf.setFontSize(12);
        pdf.setFont("helvetica", "normal");
        pdf.text("No exercises added to this routine yet.", margin, yPosition);
      }

      // Footer
      const now = new Date();
      pdf.setFontSize(8);
      pdf.setFont("helvetica", "italic");
      pdf.text(`Generated by FitFlow on ${now.toLocaleDateString()} at ${now.toLocaleTimeString()}`, 
        margin, pdf.internal.pageSize.getHeight() - 10);

      // Generate safe filename
      const safeFilename = routine.name ? 
        routine.name.replace(/[^a-z0-9\s]/gi, '').replace(/\s+/g, '_').toLowerCase() : 
        'routine_export';

      pdf.save(`${safeFilename}.pdf`);

      toast({
        title: "PDF Generated",
        description: "Routine exported successfully with organized layout!",
      });
    } catch (error) {
      console.error("PDF generation error:", error);
      toast({
        title: "Export Failed",
        description: `Failed to generate PDF: ${error instanceof Error ? error.message : 'Unknown error'}`,
        variant: "destructive",
      });
    } finally {
      setIsExporting(false);
    }
  };

  const shareViaEmail = () => {
    const url = generateShareUrl();
    const subject = encodeURIComponent(`Check out this workout routine: ${routineName}`);
    const body = encodeURIComponent(`I'd like to share this workout routine with you:\n\n${routineName}\n\nView it here: ${url}\n\nGenerated by FitFlow`);
    window.open(`mailto:?subject=${subject}&body=${body}`);
  };

  return (
    <Dialog open={isOpen} onOpenChange={setIsOpen}>
      <DialogTrigger asChild>
        <Button variant="outline" className={className} data-testid="export-routine-trigger">
          <Share2 className="w-4 h-4 mr-2" />
          Export & Share
        </Button>
      </DialogTrigger>
      <DialogContent className="sm:max-w-[600px]" aria-describedby="export-routine-description">
        <DialogHeader>
          <DialogTitle>Export & Share Routine</DialogTitle>
          <DialogDescription id="export-routine-description">
            Export your routine as PDF or share it with others
          </DialogDescription>
        </DialogHeader>

        <Tabs defaultValue="export" className="w-full">
          <TabsList className="grid w-full grid-cols-2">
            <TabsTrigger value="export" data-testid="tab-export">Export PDF</TabsTrigger>
            <TabsTrigger value="share" data-testid="tab-share">Share Routine</TabsTrigger>
          </TabsList>

          <TabsContent value="export" className="space-y-4">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center">
                  <FileText className="w-5 h-5 mr-2" />
                  PDF Export
                </CardTitle>
                <CardDescription>
                  Generate a professional PDF with organized spreadsheet-style layout
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                {isLoading ? (
                  <div className="text-center py-4">Loading routine details...</div>
                ) : routine ? (
                  <>
                    <div className="space-y-2">
                      <h4 className="font-semibold">{routine.name}</h4>
                      {routine.description && (
                        <p className="text-sm text-gray-600">{routine.description}</p>
                      )}
                      <div className="flex items-center space-x-4 text-sm text-gray-500">
                        <span>{formatDuration(routine.totalDuration)}</span>
                        <span>{routine.exerciseCount} exercises</span>
                        {routine.classType && (
                          <Badge variant="outline">{routine.classType.name}</Badge>
                        )}
                      </div>
                    </div>
                    <Button
                      onClick={exportToPDF}
                      disabled={isExporting}
                      className="w-full"
                      data-testid="button-export-pdf"
                    >
                      <Download className="w-4 h-4 mr-2" />
                      {isExporting ? "Generating PDF..." : "Download PDF"}
                    </Button>
                  </>
                ) : (
                  <div className="text-center py-4 text-gray-500">
                    Failed to load routine details
                  </div>
                )}
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="share" className="space-y-4">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center">
                  <Link2 className="w-5 h-5 mr-2" />
                  Share Link
                </CardTitle>
                <CardDescription>
                  Generate a shareable link for this routine
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="space-y-2">
                  <Label htmlFor="share-url">Shareable URL</Label>
                  <div className="flex space-x-2">
                    <Input
                      id="share-url"
                      value={shareUrl || generateShareUrl()}
                      readOnly
                      data-testid="input-share-url"
                    />
                    <Button
                      variant="outline"
                      onClick={() => copyToClipboard(shareUrl || generateShareUrl())}
                      data-testid="button-copy-url"
                    >
                      {copied ? <Check className="w-4 h-4" /> : <Copy className="w-4 h-4" />}
                    </Button>
                  </div>
                </div>

                <div className="grid grid-cols-1 gap-2">
                  <Button
                    variant="outline"
                    onClick={shareViaEmail}
                    className="justify-start"
                    data-testid="button-share-email"
                  >
                    <Mail className="w-4 h-4 mr-2" />
                    Share via Email
                  </Button>
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardHeader>
                <CardTitle>Share Options</CardTitle>
                <CardDescription>
                  Additional ways to share your routine
                </CardDescription>
              </CardHeader>
              <CardContent>
                <div className="space-y-3 text-sm text-gray-600">
                  <div className="flex items-start space-x-2">
                    <div className="w-2 h-2 bg-blue-500 rounded-full mt-2 flex-shrink-0"></div>
                    <p>Share the link with clients or fellow trainers</p>
                  </div>
                  <div className="flex items-start space-x-2">
                    <div className="w-2 h-2 bg-green-500 rounded-full mt-2 flex-shrink-0"></div>
                    <p>Recipients can view the routine without an account</p>
                  </div>
                  <div className="flex items-start space-x-2">
                    <div className="w-2 h-2 bg-purple-500 rounded-full mt-2 flex-shrink-0"></div>
                    <p>Combine with PDF export for complete workout packages</p>
                  </div>
                </div>
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/global-search.tsx.content:
import { useState, useEffect, useRef, useMemo } from "react";
import { useQuery } from "@tanstack/react-query";
import { Search, X, Clock, ListCheck, Dumbbell, Zap } from "lucide-react";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { useLocation } from "wouter";

interface SearchResult {
  id: string;
  type: 'routine' | 'exercise';
  title: string;
  description?: string;
  category?: string;
  difficulty?: string;
  duration?: number;
  exerciseCount?: number;
  equipment?: string;
}

interface GlobalSearchProps {
  onClose?: () => void;
}

export default function GlobalSearch({ onClose }: GlobalSearchProps) {
  const [query, setQuery] = useState("");
  const [isOpen, setIsOpen] = useState(false);
  const [, setLocation] = useLocation();
  const searchRef = useRef<HTMLDivElement>(null);
  const inputRef = useRef<HTMLInputElement>(null);

  // Fetch routines and exercises for search
  const { data: routines = [] } = useQuery({
    queryKey: ["/api/routines"],
  });

  const { data: exercises = [] } = useQuery({
    queryKey: ["/api/exercises"],
  });

  // Combine and filter results
  const searchResults: SearchResult[] = useMemo(() => {
    if (!query.trim()) return [];

    const searchTerm = query.toLowerCase();
    const results: SearchResult[] = [];

    // Search routines
    (routines as any[]).forEach((routine: any) => {
      if (
        routine.name?.toLowerCase().includes(searchTerm) ||
        routine.description?.toLowerCase().includes(searchTerm) ||
        routine.classType?.name?.toLowerCase().includes(searchTerm)
      ) {
        results.push({
          id: routine.id,
          type: 'routine',
          title: routine.name,
          description: routine.description,
          category: routine.classType?.name,
          duration: routine.totalDuration,
          exerciseCount: routine.exerciseCount,
        });
      }
    });

    // Search exercises
    (exercises as any[]).forEach((exercise: any) => {
      if (
        exercise.name?.toLowerCase().includes(searchTerm) ||
        exercise.description?.toLowerCase().includes(searchTerm) ||
        exercise.category?.toLowerCase().includes(searchTerm) ||
        exercise.equipment?.toLowerCase().includes(searchTerm)
      ) {
        results.push({
          id: exercise.id,
          type: 'exercise',
          title: exercise.name,
          description: exercise.description,
          category: exercise.category,
          difficulty: exercise.difficulty,
          equipment: exercise.equipment,
        });
      }
    });

    return results.slice(0, 10); // Limit results
  }, [query, routines, exercises]);

  // Handle keyboard shortcuts
  useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      // Cmd/Ctrl + K to open search
      if ((e.metaKey || e.ctrlKey) && e.key === 'k') {
        e.preventDefault();
        setIsOpen(true);
        setTimeout(() => inputRef.current?.focus(), 100);
      }

      // Escape to close
      if (e.key === 'Escape' && isOpen) {
        setIsOpen(false);
        setQuery("");
        onClose?.();
      }
    };

    document.addEventListener('keydown', handleKeyDown);
    return () => document.removeEventListener('keydown', handleKeyDown);
  }, [isOpen, onClose]);

  // Click outside to close
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (searchRef.current && !searchRef.current.contains(event.target as Node)) {
        setIsOpen(false);
        setQuery("");
      }
    };

    if (isOpen) {
      document.addEventListener('mousedown', handleClickOutside);
    }

    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, [isOpen]);

  const handleResultClick = (result: SearchResult) => {
    if (result.type === 'routine') {
      setLocation('/routines');
    } else {
      setLocation('/exercises');
    }
    setIsOpen(false);
    setQuery("");
    onClose?.();
  };

  const formatDuration = (minutes: number) => {
    if (minutes < 60) return `${minutes}min`;
    const hours = Math.floor(minutes / 60);
    const mins = minutes % 60;
    return mins > 0 ? `${hours}h ${mins}min` : `${hours}h`;
  };

  return (
    <div className="relative" ref={searchRef}>
      {/* Search Trigger Button */}
      <Button
        variant="outline"
        onClick={() => {
          setIsOpen(true);
          setTimeout(() => inputRef.current?.focus(), 100);
        }}
        className="w-full md:w-80 justify-between text-gray-500 bg-white hover:bg-gray-50"
        data-testid="global-search-trigger"
      >
        <div className="flex items-center">
          <Search className="w-4 h-4 mr-2" />
          <span>Search routines, exercises...</span>
        </div>
        <div className="hidden md:flex items-center space-x-1">
          <kbd className="px-2 py-0.5 text-xs bg-gray-100 rounded">âŒ˜</kbd>
          <kbd className="px-2 py-0.5 text-xs bg-gray-100 rounded">K</kbd>
        </div>
      </Button>

      {/* Search Modal/Dropdown */}
      {isOpen && (
        <div className="fixed inset-0 z-50 bg-black/20 backdrop-blur-sm md:relative md:inset-auto md:bg-transparent md:backdrop-blur-none">
          <div className="fixed top-20 left-4 right-4 md:absolute md:top-full md:left-0 md:right-auto md:w-96 md:mt-2">
            <Card className="shadow-xl">
              <CardContent className="p-0">
                {/* Search Input */}
                <div className="flex items-center border-b p-4">
                  <Search className="w-4 h-4 text-gray-400 mr-3" />
                  <Input
                    ref={inputRef}
                    value={query}
                    onChange={(e) => setQuery(e.target.value)}
                    placeholder="Search routines, exercises..."
                    className="border-none shadow-none focus-visible:ring-0 p-0"
                    data-testid="global-search-input"
                  />
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => {
                      setIsOpen(false);
                      setQuery("");
                      onClose?.();
                    }}
                    data-testid="global-search-close"
                  >
                    <X className="w-4 h-4" />
                  </Button>
                </div>

                {/* Search Results */}
                <div className="max-h-96 overflow-y-auto">
                  {query.trim() && searchResults.length === 0 ? (
                    <div className="p-4 text-center text-gray-500" data-testid="search-no-results">
                      <Search className="w-8 h-8 mx-auto mb-2 text-gray-300" />
                      <p>No results found for "{query}"</p>
                    </div>
                  ) : query.trim() ? (
                    <div className="py-2">
                      {searchResults.map((result) => (
                        <button
                          key={`${result.type}-${result.id}`}
                          onClick={() => handleResultClick(result)}
                          className="w-full text-left p-4 hover:bg-gray-50 transition-colors"
                          data-testid={`search-result-${result.type}-${result.id}`}
                        >
                          <div className="flex items-start space-x-3">
                            <div className={`w-8 h-8 rounded-lg flex items-center justify-center mt-0.5 ${
                              result.type === 'routine' 
                                ? 'bg-blue-100 text-blue-600' 
                                : 'bg-green-100 text-green-600'
                            }`}>
                              {result.type === 'routine' ? (
                                <ListCheck className="w-4 h-4" />
                              ) : (
                                <Dumbbell className="w-4 h-4" />
                              )}
                            </div>
                            <div className="flex-1 min-w-0">
                              <h4 className="font-medium text-gray-900 truncate">
                                {result.title}
                              </h4>
                              {result.description && (
                                <p className="text-sm text-gray-600 truncate mt-1">
                                  {result.description}
                                </p>
                              )}
                              <div className="flex items-center space-x-2 mt-2">
                                <Badge variant="secondary" className="text-xs">
                                  {result.type}
                                </Badge>
                                {result.category && (
                                  <Badge variant="outline" className="text-xs">
                                    {result.category}
                                  </Badge>
                                )}
                                {result.difficulty && (
                                  <Badge variant="outline" className="text-xs">
                                    {result.difficulty}
                                  </Badge>
                                )}
                                {result.duration && (
                                  <span className="text-xs text-gray-500 flex items-center">
                                    <Clock className="w-3 h-3 mr-1" />
                                    {formatDuration(result.duration)}
                                  </span>
                                )}
                                {result.exerciseCount && (
                                  <span className="text-xs text-gray-500 flex items-center">
                                    <Zap className="w-3 h-3 mr-1" />
                                    {result.exerciseCount} exercises
                                  </span>
                                )}
                              </div>
                            </div>
                          </div>
                        </button>
                      ))}
                    </div>
                  ) : (
                    <div className="p-4 text-center text-gray-500">
                      <Search className="w-8 h-8 mx-auto mb-2 text-gray-300" />
                      <p className="text-sm">Start typing to search routines and exercises</p>
                      <p className="text-xs mt-1">Use âŒ˜K to quickly open search</p>
                    </div>
                  )}
                </div>
              </CardContent>
            </Card>
          </div>
        </div>
      )}
    </div>
  );
}

