import { useAuth } from "@/hooks/useAuth";
import { Button } from "@/components/ui/button";
import { useLocation } from "wouter";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { 
  DropdownMenu, 
  DropdownMenuContent, 
  DropdownMenuItem, 
  DropdownMenuTrigger 
} from "@/components/ui/dropdown-menu";
import { Dumbbell, Bell, LogOut } from "lucide-react";
import { cn } from "@/lib/utils";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import GlobalSearch from "@/components/global-search";

interface NavigationProps {
  currentTab: string;
  onTabChange: (tab: string) => void;
}

const navigation = [
  { id: 'dashboard', name: 'Dashboard', icon: null },
  { id: 'classes', name: 'Classes', icon: null },
  { id: 'routines', name: 'Routines', icon: null },
  { id: 'exercises', name: 'Exercises', icon: null },
  { id: 'programs', name: 'Programs', icon: null },
  { id: 'calendar', name: 'Calendar', icon: null },
  { id: 'clients', name: 'Clients', icon: null },
  { id: 'presentation', name: 'Present', icon: null },
];

export default function Navigation({ currentTab, onTabChange }: NavigationProps) {
  const { user } = useAuth();
  const [, navigate] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const logoutMutation = useMutation({
    mutationFn: () => apiRequest('/api/auth/logout', { method: 'POST' }),
    onSuccess: () => {
      localStorage.removeItem('auth_token');
      queryClient.clear();
      toast({
        title: "Logged out",
        description: "You have been successfully logged out.",
      });
      window.location.href = '/';
    },
    onError: () => {
      // Clear local data even if API call fails
      localStorage.removeItem('auth_token');
      queryClient.clear();
      window.location.href = '/';
    },
  });

  const getInitials = (firstName?: string, lastName?: string) => {
    return `${firstName?.charAt(0) || ''}${lastName?.charAt(0) || ''}`.toUpperCase() || 'U';
  };

  return (
    <nav className="bg-white shadow-sm border-b border-border" data-testid="navigation-main">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-16">
          <div className="flex items-center">
            <button 
              className="flex-shrink-0 flex items-center hover:opacity-80 transition-opacity"
              onClick={() => {
                // Create a special route for landing page that shows for authenticated users
                window.location.href = '/landing';
              }}
              data-testid="button-logo-home"
            >
              <Dumbbell className="text-primary text-xl mr-3" />
              <span className="text-xl font-bold text-gray-900">FitFlow</span>
            </button>
            <div className="hidden md:block ml-10">
              <div className="flex items-baseline space-x-4">
                {navigation.map((item) => (
                  <button
                    key={item.id}
                    onClick={() => {
                      onTabChange(item.id);
                      navigate(item.id === 'dashboard' ? '/' : `/${item.id}`);
                    }}
                    className={cn(
                      "px-3 py-2 rounded-md text-sm font-medium transition-colors",
                      currentTab === item.id
                        ? "bg-primary/10 text-primary"
                        : "text-gray-500 hover:text-gray-700 hover:bg-gray-100"
                    )}
                    data-testid={`nav-${item.id}`}
                  >
                    {item.name}
                  </button>
                ))}
              </div>
            </div>
          </div>

          <div className="flex items-center space-x-4">
            {/* Global Search */}
            <div className="hidden md:block">
              <GlobalSearch />
            </div>



            <Button 
              variant="ghost" 
              size="sm" 
              className="p-2"
              data-testid="button-notifications"
            >
              <Bell className="h-5 w-5" />
            </Button>

            <DropdownMenu>
              <DropdownMenuTrigger asChild>
                <Button variant="ghost" className="relative h-8 w-8 rounded-full" data-testid="button-user-menu">
                  <Avatar className="h-8 w-8">
                    <AvatarImage src={(user as any)?.profileImageUrl || ''} alt="Profile" />
                    <AvatarFallback>{getInitials((user as any)?.firstName, (user as any)?.lastName)}</AvatarFallback>
                  </Avatar>
                </Button>
              </DropdownMenuTrigger>
              <DropdownMenuContent className="w-56" align="end" forceMount>

                <DropdownMenuItem 
                  className="w-full flex items-center cursor-pointer"
                  onClick={() => logoutMutation.mutate()}
                  data-testid="button-logout"
                >
                  <LogOut className="mr-2 h-4 w-4" />
                  <span>Log out</span>
                </DropdownMenuItem>
              </DropdownMenuContent>
            </DropdownMenu>
          </div>
        </div>
      </div>
    </nav>
  );
}

client/src/components/routine-builder.ts.content:
import { useState, useEffect, useCallback } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger, DialogDescription } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { ChevronUp, ChevronDown, Trash2, Plus, Search } from "lucide-react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { isUnauthorizedError } from "@/lib/authUtils";
import { insertExerciseSchema, type Exercise, type RoutineExercise, type ClassType } from "@shared/schema";
import { z } from "zod";

const exerciseFormSchema = insertExerciseSchema;

type ExerciseFormData = z.infer<typeof exerciseFormSchema>;

interface RoutineBuilderProps {
  exercises: Exercise[];
  classTypes: ClassType[];
  routineExercises: (RoutineExercise & { exercise: Exercise })[];
  onAddExercise: (exerciseId: string, orderIndex: number) => void;
  onUpdateExercise: (id: string, data: Partial<RoutineExercise>) => void;
  onRemoveExercise: (id: string) => void;
  onReorderExercises: (fromIndex: number, toIndex: number) => void;
  routineName: string;
  onRoutineNameChange: (name: string) => void;
  totalDuration: number;
  classTypeId?: string;
  onClassTypeChange: (classTypeId: string) => void;
}

export default function RoutineBuilder({
  exercises,
  classTypes,
  routineExercises,
  onAddExercise,
  onUpdateExercise,
  onRemoveExercise,
  onReorderExercises,
  routineName,
  onRoutineNameChange,
  totalDuration,
  classTypeId,
  onClassTypeChange,
}: RoutineBuilderProps) {
  // Debug logging - remove after testing
  // console.log("RoutineBuilder classTypes:", classTypes);
  // console.log("RoutineBuilder classTypes length:", classTypes?.length);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("");
  const [draggedItem, setDraggedItem] = useState<string | null>(null);
  const [isCreateExerciseDialogOpen, setIsCreateExerciseDialogOpen] = useState(false);
  const [editingExercise, setEditingExercise] = useState<RoutineExercise | null>(null);
  const [isEditDialogOpen, setIsEditDialogOpen] = useState(false);

  // Local state for debounced input values
  const [localValues, setLocalValues] = useState<Record<string, any>>({});
  const [debounceTimeouts, setDebounceTimeouts] = useState<Record<string, NodeJS.Timeout>>({});

  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Debounced update function
  const debouncedUpdate = useCallback((exerciseId: string, field: string, value: any) => {
    // Clear existing timeout for this field
    if (debounceTimeouts[`${exerciseId}-${field}`]) {
      clearTimeout(debounceTimeouts[`${exerciseId}-${field}`]);
    }

    // Set new timeout
    const timeoutId = setTimeout(() => {
      onUpdateExercise(exerciseId, { [field]: value });
      // Clean up timeout reference
      setDebounceTimeouts(prev => {
        const newTimeouts = { ...prev };
        delete newTimeouts[`${exerciseId}-${field}`];
        return newTimeouts;
      });
    }, 500); // 500ms debounce

    setDebounceTimeouts(prev => ({
      ...prev,
      [`${exerciseId}-${field}`]: timeoutId
    }));
  }, [onUpdateExercise, debounceTimeouts]);

  // Handle local input changes
  const handleLocalChange = useCallback((exerciseId: string, field: string, value: any) => {
    // Update local state immediately for responsive UI
    setLocalValues(prev => ({
      ...prev,
      [`${exerciseId}-${field}`]: value
    }));

    // Trigger debounced API update
    debouncedUpdate(exerciseId, field, value);
  }, [debouncedUpdate]);

  // Get current value (local or from props)
  const getCurrentValue = useCallback((exerciseId: string, field: string, defaultValue: any) => {
    const localKey = `${exerciseId}-${field}`;
    return localValues[localKey] !== undefined ? localValues[localKey] : defaultValue;
  }, [localValues]);

  // Clean up timeouts on unmount
  useEffect(() => {
    return () => {
      Object.values(debounceTimeouts).forEach(clearTimeout);
    };
  }, [debounceTimeouts]);

  const exerciseForm = useForm<ExerciseFormData>({
    resolver: zodResolver(exerciseFormSchema),
    defaultValues: {
      name: "",
      description: "",
      difficultyLevel: "Beginner",
      equipmentNeeded: "",
      primaryMuscles: "",
      secondaryMuscles: "",
      category: "strength",
      caloriesPerMinute: 5,
      modifications: "",
      safetyNotes: "",
      classTypeId: "none",
    },
  });

  // Create exercise mutation
  const createExercise = useMutation({
    mutationFn: async (data: ExerciseFormData) => {
      // Convert "none" to null for the API
      const processedData = {
        ...data,
        classTypeId: data.classTypeId === "none" ? null : data.classTypeId
      };
      return await apiRequest("POST", "/api/exercises", processedData);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/exercises"] });
      setIsCreateExerciseDialogOpen(false);
      exerciseForm.reset();
      toast({
        title: "Success",
        description: "Exercise created successfully!",
      });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to create exercise. Please try again.",
        variant: "destructive",
      });
    },
  });

  const onExerciseSubmit = (data: ExerciseFormData) => {
    createExercise.mutate(data);
  };

  const handleCloseExerciseDialog = () => {
    setIsCreateExerciseDialogOpen(false);
    exerciseForm.reset();
  };

  const filteredExercises = exercises.filter(exercise => {
    const matchesSearch = exercise.name.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesCategory = !selectedCategory || exercise.category === selectedCategory;
    // Filter by routine's class type if one is selected - show only exercises for that class
    const matchesClassType = !classTypeId || exercise.classTypeId === classTypeId;
    return matchesSearch && matchesCategory && matchesClassType;
  });

  const formatDuration = (seconds: number) => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return remainingSeconds > 0 ? `${minutes}m ${remainingSeconds}s` : `${minutes}m`;
  };

  const getCategoryIcon = (category: string) => {
    switch (category) {
      case 'strength':
        return 'ðŸ’ª';
      case 'cardio':
        return 'â¤ï¸';
      case 'flexibility':
        return 'ðŸ§˜';
      case 'balance':
        return 'âš–ï¸';
      default:
        return 'ðŸƒ';
    }
  };

  const getCategoryColor = (category: string) => {
    switch (category) {
      case 'strength':
        return 'bg-red-100 text-red-600';
      case 'cardio':
        return 'bg-blue-100 text-blue-600';
      case 'flexibility':
        return 'bg-green-100 text-green-600';
      case 'balance':
        return 'bg-purple-100 text-purple-600';
      default:
        return 'bg-gray-100 text-gray-600';
    }
  };

  const handleDragStart = (e: React.DragEvent, exerciseId: string) => {
    setDraggedItem(exerciseId);
    e.dataTransfer.effectAllowed = 'move';
  };

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    e.dataTransfer.dropEffect = 'move';
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    if (draggedItem) {
      const exercise = exercises.find(ex => ex.id === draggedItem);
      if (exercise) {
        onAddExercise(exercise.id, routineExercises.length);
      }
      setDraggedItem(null);
    }
  };

  const handleExerciseReorder = (fromIndex: number, toIndex: number) => {
    onReorderExercises(fromIndex, toIndex);
  };

  return (
    <div className="grid grid-cols-1 lg:grid-cols-3 gap-8" data-testid="routine-builder">
      {/* Exercise Library */}
      <div className="lg:col-span-1">
        <Card className="h-fit">
          <CardHeader>
            <div className="flex items-center justify-between mb-4">
              <div>
                <CardTitle className="text-lg">Exercise Library</CardTitle>
                {classTypeId && (
                  <p className="text-sm text-muted-foreground mt-1">
                    Showing exercises for: {classTypes.find(ct => ct.id === classTypeId)?.name || 'Selected class'}
                  </p>
                )}
              </div>
              <Dialog open={isCreateExerciseDialogOpen} onOpenChange={setIsCreateExerciseDialogOpen}>
                <DialogTrigger asChild>
                  <Button size="sm" className="bg-primary hover:bg-primary/90" data-testid="button-add-new-exercise">
                    <Plus className="w-4 h-4 mr-1" />
                    Add New
                  </Button>
                </DialogTrigger>
                <DialogContent className="sm:max-w-[600px] max-h-[90vh] overflow-y-auto">
                  <DialogHeader>
                    <DialogTitle>Create New Exercise</DialogTitle>
                    <DialogDescription>
                      Add a new exercise to your library that you can use in routines.
                    </DialogDescription>
                  </DialogHeader>
                  <Form {...exerciseForm}>
                    <form onSubmit={exerciseForm.handleSubmit(onExerciseSubmit)} className="space-y-4">
                      <FormField
                        control={exerciseForm.control}
                        name="name"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Exercise Name</FormLabel>
                            <FormControl>
                              <Input placeholder="Enter exercise name" {...field} data-testid="input-new-exercise-name" />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <FormField
                        control={exerciseForm.control}
                        name="description"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Description</FormLabel>
                            <FormControl>
                              <Textarea 
                                placeholder="Describe the exercise..." 
                                {...field}
                                value={field.value || ""}
                                data-testid="input-new-exercise-description"
                              />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <div className="grid grid-cols-3 gap-4">
                        <FormField
                          control={exerciseForm.control}
                          name="category"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Category</FormLabel>
                              <Select onValueChange={field.onChange} defaultValue={field.value}>
                                <FormControl>
                                  <SelectTrigger data-testid="select-new-exercise-category">
                                    <SelectValue placeholder="Select category" />
                                  </SelectTrigger>
                                </FormControl>
                                <SelectContent>
                                  <SelectItem value="strength">Strength</SelectItem>
                                  <SelectItem value="cardio">Cardio</SelectItem>
                                  <SelectItem value="flexibility">Flexibility</SelectItem>
                                  <SelectItem value="balance">Balance</SelectItem>
                                  <SelectItem value="sports">Sports</SelectItem>
                                  <SelectItem value="rehabilitation">Rehabilitation</SelectItem>
                                </SelectContent>
                              </Select>
                              <FormMessage />
                            </FormItem>
                          )}
                        />

                        <FormField
                          control={exerciseForm.control}
                          name="difficultyLevel"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Difficulty</FormLabel>
                              <Select onValueChange={field.onChange} defaultValue={field.value}>
                                <FormControl>
                                  <SelectTrigger data-testid="select-new-exercise-difficulty">
                                    <SelectValue placeholder="Select difficulty" />
                                  </SelectTrigger>
                                </FormControl>
                                <SelectContent>
                                  <SelectItem value="Beginner">Beginner</SelectItem>
                                  <SelectItem value="Intermediate">Intermediate</SelectItem>
                                  <SelectItem value="Advanced">Advanced</SelectItem>
                                </SelectContent>
                              </Select>
                              <FormMessage />
                            </FormItem>
                          )}
                        />

                        <FormField
                          control={exerciseForm.control}
                          name="classTypeId"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Class Type</FormLabel>
                              <Select onValueChange={field.onChange} defaultValue={field.value || "none"}>
                                <FormControl>
                                  <SelectTrigger data-testid="select-new-exercise-class-type">
                                    <SelectValue placeholder="Select class type" />
                                  </SelectTrigger>
                                </FormControl>
                                <SelectContent>
                                  <SelectItem value="none">No specific class</SelectItem>
                                  {classTypes && classTypes.length > 0 ? (
                                    classTypes.map((classType) => (
                                      <SelectItem key={classType.id} value={classType.id}>
                                        {classType.name}
                                      </SelectItem>
                                    ))
                                  ) : (
                                    <SelectItem value="no-types" disabled>No class types available</SelectItem>
                                  )}
                                </SelectContent>
                              </Select>
                              <FormMessage />
                            </FormItem>
                          )}
                        />
                      </div>

                      <div className="grid grid-cols-2 gap-4">
                        <FormField
                          control={exerciseForm.control}
                          name="equipmentNeeded"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Equipment Needed</FormLabel>
                              <FormControl>
                                <Input placeholder="e.g., Dumbbells, Mat" {...field} value={field.value || ""} data-testid="input-new-exercise-equipment" />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />

                        <FormField
                          control={exerciseForm.control}
                          name="caloriesPerMinute"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Calories per Minute</FormLabel>
                              <FormControl>
                                <Input 
                                  type="number" 
                                  placeholder="5" 
                                  {...field}
                                  value={field.value || ""}
                                  onChange={(e) => field.onChange(parseInt(e.target.value) || 0)}
                                  data-testid="input-new-exercise-calories"
                                />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />
                      </div>

                      <FormField
                        control={exerciseForm.control}
                        name="primaryMuscles"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Primary Muscles</FormLabel>
                            <FormControl>
                              <Input placeholder="e.g., Chest, Shoulders" {...field} value={field.value || ""} data-testid="input-new-exercise-primary" />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <FormField
                        control={exerciseForm.control}
                        name="modifications"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Modifications (Optional)</FormLabel>
                            <FormControl>
                              <Textarea 
                                placeholder="Alternative variations for beginners or advanced..." 
                                {...field}
                                value={field.value || ""}
