import { useState } from "react";
import { useAuth } from "@/hooks/useAuth";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { insertClientSchema, type Client, type InsertClient } from "@shared/schema";
import { Plus, User, Phone, Mail, Calendar, Target, AlertTriangle, Users, Search } from "lucide-react";
import { format } from "date-fns";

// Attendance Tab Component
function AttendanceTab({ clientId }: { clientId: string }) {
  const queryClient = useQueryClient();

  const { data: attendance = [], isLoading } = useQuery({
    queryKey: ["/api/attendance/client", clientId],
  });

  // Get only events where this client is enrolled
  const { data: enrolledEvents = [] } = useQuery({
    queryKey: [`/api/calendar/events/client/${clientId}`],
  });

  const checkInMutation = useMutation({
    mutationFn: async (data: { eventId: string; status: string }) => {
      const response = await apiRequest("POST", "/api/attendance", {
        clientId,
        eventId: data.eventId,
        status: data.status,
        checkedInAt: new Date(),
      });
      return await response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/attendance/client", clientId] });
    },
  });

  if (isLoading) {
    return <div>Loading attendance...</div>;
  }

  const upcomingForClient = enrolledEvents.filter((event: any) => 
    new Date(event.startDatetime) > new Date()
  ).slice(0, 5);

  return (
    <div className="space-y-6">
      {/* Quick Check-in Section */}
      <div>
        <h4 className="font-medium mb-3">Enrolled Classes</h4>
        {upcomingForClient.length === 0 ? (
          <p className="text-gray-500 text-sm">Not enrolled in any upcoming classes</p>
        ) : (
          <div className="space-y-2">
            {upcomingForClient.map((event: any) => (
              <div key={event.id} className="flex items-center justify-between p-3 border rounded-lg">
                <div>
                  <p className="font-medium">{event.title}</p>
                  <p className="text-sm text-gray-500">
                    {format(new Date(event.startDatetime), "MMM d, yyyy 'at' h:mm a")}
                  </p>
                </div>
                <Button
                  size="sm"
                  onClick={() => checkInMutation.mutate({ 
                    eventId: event.id, 
                    status: "checked_in" 
                  })}
                  disabled={checkInMutation.isPending}
                  data-testid={`button-checkin-${event.id}`}
                >
                  Check In
                </Button>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Attendance History */}
      <div>
        <h4 className="font-medium mb-3">Attendance History</h4>
        {attendance.length === 0 ? (
          <div className="text-center py-8">
            <Calendar className="mx-auto h-8 w-8 text-gray-400 mb-2" />
            <p className="text-gray-500">No attendance records yet</p>
          </div>
        ) : (
          <div className="space-y-2">
            {attendance.map((record: any) => (
              <div key={record.id} className="flex items-center justify-between p-3 border rounded-lg">
                <div>
                  <p className="font-medium">{record.event?.title || 'Class'}</p>
                  <p className="text-sm text-gray-500">
                    {record.checkedInAt ? 
                      format(new Date(record.checkedInAt), "MMM d, yyyy 'at' h:mm a") :
                      format(new Date(record.createdAt), "MMM d, yyyy")
                    }
                  </p>
                </div>
                <Badge 
                  variant={record.status === "checked_in" ? "default" : 
                          record.status === "no_show" ? "destructive" : "secondary"}
                >
                  {record.status.replace('_', ' ')}
                </Badge>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default function Clients() {
  const { isAuthenticated, isLoading } = useAuth();
  const [isCreateDialogOpen, setIsCreateDialogOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedClient, setSelectedClient] = useState<Client | null>(null);
  const queryClient = useQueryClient();
  const { toast } = useToast();

  // Fetch clients
  const { data: clients = [], isLoading: clientsLoading } = useQuery({
    queryKey: ["/api/clients"],
    enabled: isAuthenticated && !isLoading,
  });

  // Create client form
  const form = useForm<InsertClient>({
    resolver: zodResolver(insertClientSchema.omit({ trainerId: true })),
    defaultValues: {
      firstName: "",
      lastName: "",
      email: "",
      phone: "",
      goals: "",
      medicalConditions: "",
      injuries: "",
      fitnessLevel: "Beginner",
      notes: "",
    },
  });

  // Create client mutation
  const createClientMutation = useMutation({
    mutationFn: async (data: InsertClient) => {
      const response = await apiRequest("POST", "/api/clients", data);
      return await response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/clients"] });
      setIsCreateDialogOpen(false);
      form.reset();
      toast({
        title: "Client created",
        description: "New client has been added successfully.",
      });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: "Failed to create client. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleCreateClient = (data: InsertClient) => {
    createClientMutation.mutate(data);
  };

  // Filter clients based on search  
  const filteredClients = Array.isArray(clients) ? clients.filter((client: Client) =>
    `${client.firstName} ${client.lastName}`.toLowerCase().includes(searchQuery.toLowerCase()) ||
    client.email?.toLowerCase().includes(searchQuery.toLowerCase())
  ) : [];

  if (isLoading) {
    return <div>Loading...</div>;
  }

  if (!isAuthenticated) {
    return <div>Please log in to access clients.</div>;
  }

  return (
    <main className="flex-1 p-6">
      <div className="flex justify-between items-center mb-6">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">Client Management</h1>
          <p className="text-gray-600 mt-1">Manage your clients and track their progress</p>
        </div>
        <Dialog open={isCreateDialogOpen} onOpenChange={setIsCreateDialogOpen}>
          <DialogTrigger asChild>
            <Button data-testid="button-create-client">
              <Plus className="w-4 h-4 mr-2" />
              Add Client
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-2xl">
            <DialogHeader>
              <DialogTitle>Add New Client</DialogTitle>
              <DialogDescription>
                Create a new client profile with their personal information and fitness goals.
              </DialogDescription>
            </DialogHeader>
            <form onSubmit={form.handleSubmit(handleCreateClient)} className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="firstName">First Name</Label>
                  <Input
                    {...form.register("firstName")}
                    placeholder="John"
                    data-testid="input-first-name"
                  />
                  {form.formState.errors.firstName && (
                    <p className="text-sm text-red-600">{form.formState.errors.firstName.message}</p>
                  )}
                </div>
                <div>
                  <Label htmlFor="lastName">Last Name</Label>
                  <Input
                    {...form.register("lastName")}
                    placeholder="Doe"
                    data-testid="input-last-name"
                  />
                  {form.formState.errors.lastName && (
                    <p className="text-sm text-red-600">{form.formState.errors.lastName.message}</p>
                  )}
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="email">Email</Label>
                  <Input
                    {...form.register("email")}
                    type="email"
                    placeholder="john.doe@email.com"
                    data-testid="input-email"
                  />
                </div>
                <div>
                  <Label htmlFor="phone">Phone</Label>
                  <Input
                    {...form.register("phone")}
                    placeholder="(555) 123-4567"
                    data-testid="input-phone"
                  />
                </div>
              </div>

              <div>
                <Label htmlFor="fitnessLevel">Fitness Level</Label>
                <Select onValueChange={(value) => form.setValue("fitnessLevel", value)}>
                  <SelectTrigger data-testid="select-fitness-level">
                    <SelectValue placeholder="Select fitness level" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="Beginner">Beginner</SelectItem>
                    <SelectItem value="Intermediate">Intermediate</SelectItem>
                    <SelectItem value="Advanced">Advanced</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div>
                <Label htmlFor="goals">Goals</Label>
                <Textarea
                  {...form.register("goals")}
                  placeholder="What are their fitness goals?"
                  data-testid="input-goals"
                />
              </div>

              <div>
                <Label htmlFor="medicalConditions">Medical Conditions</Label>
                <Textarea
                  {...form.register("medicalConditions")}
                  placeholder="Any medical conditions to be aware of?"
                  data-testid="input-medical-conditions"
                />
              </div>

              <div>
                <Label htmlFor="injuries">Injuries or Limitations</Label>
                <Textarea
                  {...form.register("injuries")}
                  placeholder="Any injuries or physical limitations?"
                  data-testid="input-injuries"
                />
              </div>

              <div>
                <Label htmlFor="notes">Additional Notes</Label>
                <Textarea
                  {...form.register("notes")}
                  placeholder="Any additional notes about this client?"
                  data-testid="input-notes"
                />
              </div>

              <div className="flex justify-end gap-3">
                <Button
                  type="button"
                  variant="outline"
                  onClick={() => setIsCreateDialogOpen(false)}
                  data-testid="button-cancel"
                >
                  Cancel
                </Button>
                <Button
                  type="submit"
                  disabled={createClientMutation.isPending}
                  data-testid="button-save"
                >
                  {createClientMutation.isPending ? "Creating..." : "Create Client"}
                </Button>
              </div>
            </form>
          </DialogContent>
        </Dialog>
      </div>

      {/* Search and filter */}
      <div className="mb-6">
        <div className="relative">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
          <Input
            placeholder="Search clients by name or email..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="pl-10"
            data-testid="input-search-clients"
          />
        </div>
      </div>

      {/* Clients grid */}
      {clientsLoading ? (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {[...Array(6)].map((_, i) => (
            <Card key={i} className="animate-pulse">
              <CardHeader>
                <div className="h-4 bg-gray-200 rounded w-3/4"></div>
                <div className="h-3 bg-gray-200 rounded w-1/2"></div>
              </CardHeader>
              <CardContent>
                <div className="space-y-2">
                  <div className="h-3 bg-gray-200 rounded w-full"></div>
                  <div className="h-3 bg-gray-200 rounded w-2/3"></div>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      ) : filteredClients.length === 0 ? (
        <Card className="text-center py-12">
          <CardContent>
            <Users className="mx-auto h-12 w-12 text-gray-400 mb-4" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">No clients found</h3>
            <p className="text-gray-500 mb-4">
              {searchQuery ? "No clients match your search criteria." : "Get started by adding your first client."}
            </p>
            {!searchQuery && (
              <Button onClick={() => setIsCreateDialogOpen(true)} data-testid="button-add-first-client">
                <Plus className="w-4 h-4 mr-2" />
                Add Your First Client
              </Button>
            )}
          </CardContent>
        </Card>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredClients.map((client: Client) => (
            <Card key={client.id} className="hover:shadow-lg transition-shadow cursor-pointer" 
                  onClick={() => setSelectedClient(client)}
                  data-testid={`card-client-${client.id}`}>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <User className="h-5 w-5" />
                  {client.firstName} {client.lastName}
                </CardTitle>
                <CardDescription>
                  <Badge variant="secondary">{client.fitnessLevel}</Badge>
                </CardDescription>
              </CardHeader>
              <CardContent>
                <div className="space-y-2 text-sm">
                  {client.email && (
                    <div className="flex items-center gap-2 text-gray-600">
                      <Mail className="h-4 w-4" />
                      {client.email}
                    </div>
                  )}
                  {client.phone && (
                    <div className="flex items-center gap-2 text-gray-600">
                      <Phone className="h-4 w-4" />
                      {client.phone}
                    </div>
                  )}
                  {client.goals && (
                    <div className="flex items-start gap-2 text-gray-600">
                      <Target className="h-4 w-4 mt-0.5 flex-shrink-0" />
                      <span className="line-clamp-2">{client.goals}</span>
                    </div>
                  )}
                  {(client.medicalConditions || client.injuries) && (
                    <div className="flex items-center gap-2 text-orange-600">
                      <AlertTriangle className="h-4 w-4" />
                      <span className="text-xs">Medical notes</span>
                    </div>
                  )}
                  <div className="flex items-center gap-2 text-gray-500 text-xs">
                    <Calendar className="h-3 w-3" />
                    Added {client.createdAt ? format(new Date(client.createdAt), "MMM d, yyyy") : "Recently"}
                  </div>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      )}

      {/* Client detail modal */}
      {selectedClient && (
        <Dialog open={!!selectedClient} onOpenChange={() => setSelectedClient(null)}>
          <DialogContent className="max-w-4xl max-h-[80vh] overflow-y-auto">
            <DialogHeader>
              <DialogTitle className="flex items-center gap-2">
                <User className="h-5 w-5" />
                {selectedClient.firstName} {selectedClient.lastName}
              </DialogTitle>
            </DialogHeader>

            <Tabs defaultValue="profile" className="w-full">
              <TabsList className="grid w-full grid-cols-4">
                <TabsTrigger value="profile">Profile</TabsTrigger>
                <TabsTrigger value="progress">Progress</TabsTrigger>
                <TabsTrigger value="attendance">Attendance</TabsTrigger>
                <TabsTrigger value="notes">Notes</TabsTrigger>
              </TabsList>

              <TabsContent value="profile" className="space-y-4">
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <Label>Contact Information</Label>
                    <div className="space-y-2 mt-1">
                      {selectedClient.email && (
                        <div className="flex items-center gap-2">
                          <Mail className="h-4 w-4 text-gray-400" />
                          <span>{selectedClient.email}</span>
                        </div>
                      )}
                      {selectedClient.phone && (
                        <div className="flex items-center gap-2">
                          <Phone className="h-4 w-4 text-gray-400" />
                          <span>{selectedClient.phone}</span>
                        </div>
                      )}
                    </div>
                  </div>
                  <div>
                    <Label>Fitness Level</Label>
                    <div className="mt-1">
                      <Badge variant="secondary">{selectedClient.fitnessLevel}</Badge>
                    </div>
                  </div>
                </div>

                {selectedClient.goals && (
                  <div>
                    <Label>Goals</Label>
                    <p className="mt-1 text-sm text-gray-600">{selectedClient.goals}</p>
                  </div>
                )}

                {selectedClient.medicalConditions && (
                  <div>
                    <Label>Medical Conditions</Label>
                    <p className="mt-1 text-sm text-gray-600">{selectedClient.medicalConditions}</p>
                  </div>
                )}

                {selectedClient.injuries && (
                  <div>
                    <Label>Injuries & Limitations</Label>
                    <p className="mt-1 text-sm text-gray-600">{selectedClient.injuries}</p>
                  </div>
                )}

                {selectedClient.notes && (
                  <div>
                    <Label>Additional Notes</Label>
                    <p className="mt-1 text-sm text-gray-600">{selectedClient.notes}</p>
                  </div>
                )}
              </TabsContent>

              <TabsContent value="progress">
                <ProgressTab clientId={selectedClient.id} />
              </TabsContent>

              <TabsContent value="attendance">
                <AttendanceTab clientId={selectedClient.id} />
              </TabsContent>

              <TabsContent value="notes">
                <NotesTab clientId={selectedClient.id} />
              </TabsContent>
            </Tabs>
          </DialogContent>
        </Dialog>
      )}
    </main>
  );
}

// Progress Tab Component
function ProgressTab({ clientId }: { clientId: string }) {
  const [selectedExercise, setSelectedExercise] = useState<string>("all");
  const { data: progress = [], isLoading } = useQuery({
    queryKey: ["/api/progress-metrics/client", clientId],
  });

  const { data: exercises = [] } = useQuery({
    queryKey: ["/api/exercises"],
  });

  const [isAddingProgress, setIsAddingProgress] = useState(false);
  const queryClient = useQueryClient();

  const progressForm = useForm({
    defaultValues: {
      exerciseId: "",
      metricType: "weight",
      value: "",
      unit: "kg",
      rpe: 5,
      notes: "",
    },
  });

  const addProgressMutation = useMutation({
    mutationFn: async (data: any) => {
      const response = await apiRequest("POST", "/api/progress-metrics", {
        clientId,
        ...data,
      });
      return await response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/progress-metrics/client", clientId] });
      setIsAddingProgress(false);
      progressForm.reset();
    },
  });

  const handleAddProgress = (data: any) => {
    addProgressMutation.mutate(data);
  };

  if (isLoading) {
    return <div>Loading progress...</div>;
  }

  const exerciseOptions = exercises.map((ex: any) => ({ 
    id: ex.id, 
    name: ex.name 
  }));

  const filteredProgress = selectedExercise && selectedExercise !== "all"
    ? progress.filter((p: any) => p.exerciseId === selectedExercise)
    : progress;

  return (
    <div className="space-y-6">
      {/* Add Progress Section */}
      <div className="flex justify-between items-center">
        <div>
          <h4 className="font-medium">Progress Tracking</h4>
          <p className="text-sm text-gray-500">Track client's performance metrics</p>
        </div>
        <Button 
          onClick={() => setIsAddingProgress(true)}
          size="sm"
          data-testid="button-add-progress"
        >
          <Plus className="w-4 h-4 mr-2" />
          Add Progress
        </Button>
      </div>

      {/* Exercise Filter */}
      <div>
        <Label>Filter by Exercise</Label>
        <Select value={selectedExercise} onValueChange={setSelectedExercise}>
          <SelectTrigger>
            <SelectValue placeholder="All exercises" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="all">All exercises</SelectItem>
            {exerciseOptions.map((exercise: any) => (
              <SelectItem key={exercise.id} value={exercise.id}>
                {exercise.name}
              </SelectItem>
            ))}
          </SelectContent>
        </Select>
      </div>

      {/* Progress Records */}
      {filteredProgress.length === 0 ? (
        <div className="text-center py-8">
          <Target className="mx-auto h-8 w-8 text-gray-400 mb-2" />
          <p className="text-gray-500">No progress records yet</p>
        </div>
      ) : (
        <div className="space-y-3">
          {filteredProgress.map((record: any) => (
            <Card key={record.id}>
              <CardContent className="p-4">
                <div className="flex justify-between items-start">
                  <div>
                    <p className="font-medium">{record.exercise?.name || 'Exercise'}</p>
                    <p className="text-sm text-gray-500 capitalize">{record.metricType}</p>
                  </div>
                  <div className="text-right">
                    <p className="font-medium">{record.value} {record.unit}</p>
                    {record.rpe && (
                      <p className="text-sm text-gray-500">RPE: {record.rpe}/10</p>
                    )}
                  </div>
                </div>
                {record.notes && (
                  <p className="text-sm text-gray-600 mt-2">{record.notes}</p>
                )}
                <p className="text-xs text-gray-400 mt-1">
                  {format(new Date(record.recordedAt), "MMM d, yyyy 'at' h:mm a")}
                </p>
              </CardContent>
            </Card>
          ))}
        </div>
      )}

      {/* Add Progress Dialog */}
      <Dialog open={isAddingProgress} onOpenChange={setIsAddingProgress}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Add Progress Record</DialogTitle>
            <DialogDescription>
              Record a new performance metric for this client
            </DialogDescription>
          </DialogHeader>
          <form onSubmit={progressForm.handleSubmit(handleAddProgress)} className="space-y-4">
            <div>
              <Label>Exercise</Label>
              <Select onValueChange={(value) => progressForm.setValue("exerciseId", value)} required>
                <SelectTrigger>
                  <SelectValue placeholder="Select exercise" />
                </SelectTrigger>
                <SelectContent>
                  {exerciseOptions.map((exercise: any) => (
                    <SelectItem key={exercise.id} value={exercise.id}>
                      {exercise.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div className="grid grid-cols-2 gap-4">
              <div>
                <Label>Metric Type</Label>
                <Select onValueChange={(value) => progressForm.setValue("metricType", value)} defaultValue="weight">
                  <SelectTrigger>
                    <SelectValue placeholder="Select type" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="weight">Weight</SelectItem>
                    <SelectItem value="reps">Reps</SelectItem>
                    <SelectItem value="time">Time</SelectItem>
                    <SelectItem value="distance">Distance</SelectItem>
                    <SelectItem value="body_weight">Body Weight</SelectItem>
                  </SelectContent>
                </Select>
              </div>
              <div>
                <Label>Unit</Label>
                <Select onValueChange={(value) => progressForm.setValue("unit", value)} defaultValue="kg">
                  <SelectTrigger>
                    <SelectValue placeholder="Select unit" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="kg">kg</SelectItem>
                    <SelectItem value="lbs">lbs</SelectItem>
                    <SelectItem value="seconds">seconds</SelectItem>
                    <SelectItem value="minutes">minutes</SelectItem>
                    <SelectItem value="meters">meters</SelectItem>
                    <SelectItem value="km">km</SelectItem>
                    <SelectItem value="reps">reps</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>

            <div className="grid grid-cols-2 gap-4">
              <div>
                <Label>Value</Label>
                <Input
                  {...progressForm.register("value")}
                  placeholder="e.g., 100"
                  data-testid="input-progress-value"
                />
              </div>
              <div>
                <Label>RPE (1-10)</Label>
                <Input
                  {...progressForm.register("rpe", { valueAsNumber: true })}
                  type="number"
                  min="1"
                  max="10"
                  placeholder="5"
                  data-testid="input-progress-rpe"
                />
              </div>
            </div>

            <div>
              <Label>Notes</Label>
              <Textarea
                {...progressForm.register("notes")}
                placeholder="Any additional notes..."
                data-testid="input-progress-notes"
              />
            </div>

            <div className="flex justify-end gap-3">
              <Button
                type="button"
                variant="outline"
                onClick={() => setIsAddingProgress(false)}
              >
                Cancel
              </Button>
              <Button
                type="submit"
                disabled={addProgressMutation.isPending}
                data-testid="button-save-progress"
              >
                {addProgressMutation.isPending ? "Saving..." : "Save Progress"}
              </Button>
            </div>
          </form>
        </DialogContent>
      </Dialog>
    </div>
  );
}

// Notes Tab Component
function NotesTab({ clientId }: { clientId: string }) {
  const [isAddingNote, setIsAddingNote] = useState(false);
  const { data: notes = [], isLoading } = useQuery({
    queryKey: ["/api/client-notes", clientId],
  });

  const queryClient = useQueryClient();

  const noteForm = useForm({
    defaultValues: {
      note: "",
      noteType: "general",
    },
  });

  const addNoteMutation = useMutation({
    mutationFn: async (data: { note: string; noteType: string }) => {
      const response = await apiRequest("POST", "/api/client-notes", {
        clientId,
        ...data,
      });
      return await response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/client-notes", clientId] });
      setIsAddingNote(false);
      noteForm.reset();
    },
  });

  const handleAddNote = (data: { note: string; noteType: string }) => {
    addNoteMutation.mutate(data);
  };

  if (isLoading) {
    return <div>Loading notes...</div>;
  }

  const noteTypeColors = {
    general: "bg-gray-100 text-gray-800",
    progress: "bg-blue-100 text-blue-800", 
    injury: "bg-red-100 text-red-800",
    goal: "bg-green-100 text-green-800",
  };

  return (
    <div className="space-y-6">
      {/* Add Note Section */}
      <div className="flex justify-between items-center">
        <div>
          <h4 className="font-medium">Client Notes</h4>
          <p className="text-sm text-gray-500">Track important information and communications</p>
        </div>
        <Button 
          onClick={() => setIsAddingNote(true)}
          size="sm"
          data-testid="button-add-note"
        >
          <Plus className="w-4 h-4 mr-2" />
          Add Note
        </Button>
      </div>

      {/* Notes List */}
      {notes.length === 0 ? (
        <div className="text-center py-8">
          <User className="mx-auto h-8 w-8 text-gray-400 mb-2" />
          <p className="text-gray-500">No notes yet</p>
        </div>
      ) : (
        <div className="space-y-3">
          {notes.map((note: any) => (
            <Card key={note.id}>
              <CardContent className="p-4">
                <div className="flex justify-between items-start mb-2">
                  <Badge 
                    className={noteTypeColors[note.noteType as keyof typeof noteTypeColors] || noteTypeColors.general}
                    variant="secondary"
                  >
                    {note.noteType}
                  </Badge>
                  <p className="text-xs text-gray-400">
                    {format(new Date(note.createdAt), "MMM d, yyyy 'at' h:mm a")}
                  </p>
                </div>
                <p className="text-sm text-gray-700">{note.note}</p>
              </CardContent>
            </Card>
          ))}
        </div>
      )}

      {/* Add Note Dialog */}
      <Dialog open={isAddingNote} onOpenChange={setIsAddingNote}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Add Client Note</DialogTitle>
            <DialogDescription>
              Add a note about this client for future reference
            </DialogDescription>
          </DialogHeader>
          <form onSubmit={noteForm.handleSubmit(handleAddNote)} className="space-y-4">
            <div>
              <Label>Note Type</Label>
              <Select onValueChange={(value) => noteForm.setValue("noteType", value)} defaultValue="general">
                <SelectTrigger>
                  <SelectValue placeholder="Select note type" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="general">General</SelectItem>
                  <SelectItem value="progress">Progress</SelectItem>
                  <SelectItem value="injury">Injury/Medical</SelectItem>
                  <SelectItem value="goal">Goal Setting</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <div>
              <Label>Note</Label>
              <Textarea
                {...noteForm.register("note", { required: "Note is required" })}
                placeholder="Enter your note here..."
                rows={4}
                data-testid="input-note-content"
              />
              {noteForm.formState.errors.note && (
                <p className="text-sm text-red-600">{noteForm.formState.errors.note.message}</p>
              )}
            </div>

            <div className="flex justify-end gap-3">
              <Button
                type="button"
                variant="outline"
                onClick={() => setIsAddingNote(false)}
              >
                Cancel
              </Button>
              <Button
                type="submit"
                disabled={addNoteMutation.isPending}
                data-testid="button-save-note"
              >
                {addNoteMutation.isPending ? "Saving..." : "Save Note"}
              </Button>
            </div>
          </form>
        </DialogContent>
      </Dialog>
    </div>
  );
}

